event.script:
if ( data.Data_Page.CurrPosInfo.RightSide.CollegeOrg.FieldVal.rawValue == "00000000" ) {
this.presence = "hidden";
} else {
this.presence = "visible";
}

//Check ISR control parameters for read/write access
if($record.FORM_SCENARIO_STAGE.DATA.FIELD == "SENDING_MANAGER" |
	$record.FORM_SCENARIO_STAGE.DATA.FIELD == "PROCESS" )
then
	if ($record.CONTROL_PARAM.ISR_REVIEW == "true")
	then
		$.access = "readOnly"
//		data.CostOverride.access = "readOnly"
	else
		$.access = ""
//		data.CostOverride.access = ""
	endif
else
	$.access = "readOnly"
//	data.CostOverride.access = "readOnly"
endif

// DO NOT MODIFY THE CODE BEYOND THIS POINT - 705.20060123211327.269554.269544 - EnumListDropDown.xfo.p1

