	       // if the flag complete = 0 then call back to the server for a value selection
	       if (this.bind.ref != "") {
	         var DataBinding = this.bind.ref;
	         // the data binding may contain "$data.<root data name>" instead of $record
	         DataBinding = DataBinding.replace("$data."+xfa.record.name,"$record");
	         DataBinding = DataBinding.replace("$record.", "");

	         var existenceNode = xfa.form.resolveNode("xfa.datasets.data."+xfa.record.name+".sap-vhlist."+DataBinding.replace(/(\.)/g,"\\.").replace(/(\[\*\])/g,"")+".complete");
	         if (existenceNode != null && existenceNode.value == "0") {

	             var processingF4HelpOpen = ContainerFoundation_JS.getGlobalValue("processingF4HelpOpen");
	             if(processingF4HelpOpen == false) {
	               ContainerFoundation_JS.setGlobalValue("processingF4HelpOpen", true);
	               ContainerFoundation_JS.SendMessageToContainer(event.target,
	                                                            "f4HelpOpen", 
	                                                            this.dataNode.somExpression.replace(/(xfa.*?\.)(datasets.*?\.)(data.*?\.)/, ""), 
	                                                            this.rawValue, 
	                                                            event.target.mouseX,
	                                                            event.target.mouseY);
	             }
	         }
	       }
	       // END OF DO NOT MODIFY

// DO NOT MODIFY THE CODE BEYOND THIS POINT - 705.20060123211327.269554.270138 - EnumListDropDown.xfo.p3
	       // clear selection if data value is not in list
	       if (xfa.host.name == "Acrobat" && this.bind.ref != "") {
	         var DataBinding = this.bind.ref;
	         // the data binding may contain "$data.<root data name>" instead of $record
	         DataBinding = DataBinding.replace("$data."+xfa.record.name,"$record");
	         DataBinding = DataBinding.replace("$record.", "");

	         var hasItems = true;
	         var itemNode = xfa.form.resolveNode("xfa.datasets.data."+xfa.record.name+".sap-vhlist."+DataBinding.replace(/(\.)/g,"\\.").replace(/(\[\*\])/g,""));
	         var itemNodes = null;
	         try {
	           itemNodes = itemNode.nodes;
	         } catch (e) {
	           hasItems = false;
	         }

	         if(hasItems == true) {
	           var currentValue = this.rawValue;
	           var found = 0;
	           var firstValue = null;
	           for (var i = 0; i < itemNodes.length; i++) {
	             if (itemNodes.item(i).name == "item") {
	               if(firstValue == null) {
	                 firstValue = itemNodes.item(i).key.value;
	               }
	               if (itemNodes.item(i).key.value == currentValue) {
	                 found = 1;
	                 break;
	               }
	             }
	           }
	           if (found == 1) {
	             this.rawValue = currentValue;
	           } else {
	             this.rawValue = firstValue;
	             event.target.dirty = true;
	           }
	         } else {
	           this.rawValue = null;
	           event.target.dirty = true;
	         }
	       }
	       // END OF DO NOT MODIFY

