 	if (  data.Data_Page.CurrPosInfo.LeftSide.EmpGrp.EmpGrpValue.rawValue == "F" ){
 		data.Data_Page.NewMgrInstr_SF.CheckBoxes.dStuCont.presence = "visible";
 		data.Data_Page.NewMgrInstr_SF.CheckBoxes.IT16_CTEDT.presence = "hidden";
 	}else{
		data.Data_Page.NewMgrInstr_SF.CheckBoxes.dStuCont.presence = "hidden";
 		data.Data_Page.NewMgrInstr_SF.CheckBoxes.IT16_CTEDT.presence = "visible";
  	}
}else{
		data.Data_Page.NewMgrInstr_SF.CheckBoxes.dStuCont.presence = "hidden";
 		data.Data_Page.NewMgrInstr_SF.CheckBoxes.IT16_CTEDT.presence = "hidden";
}

