//compute avg daily hours
var avghours = this.rawValue / 5
data.Data_Page.NewMgrInstr_SF.WorkScheduleSF.DailyHours = Round(avghours,2)

//compute employment percentage
var emppct = Round(( $.rawValue / 37.5 ) * 100,2)
if ( emppct > 100.0) 
then
	emppct = 100.0
endif

data.Data_Page.NewMgrInstr_SF.WorkScheduleSF.FTE.rawValue = emppct
var r3emppct = $record.R3_EMPCT.DATA[0].FIELD.value
if ( r3emppct <1 ) then r3emppct = 100 endif
if ( payarea == "M1" )
then
	data.Data_Page.NewMgrInstr_SF.Salary.AnnualRate.rawValue = Round(emppct * ($record.R3_SALARY.DATA[0].FIELD.value / r3emppct ),2)
else
	data.Data_Page.NewMgrInstr_SF.Salary.AnnualRate.rawValue = Round( data.Data_Page.NewMgrInstr_SF.WorkScheduleSF.WeeklyHours.rawValue *  data.Data_Page.NewMgrInstr_SF.Salary.HourlyRate.rawValue * 52,2)
	//xfa.host.messageBox(data.Data_Page.NewMgrInstr_SF.Salary.AnnualRate.rawValue)
	$record.I0008_DIVGV.DATA[0].FIELD.value = $.rawValue * 2
endif

//get new payroll area
var payarea = xfa.record.I0001_ABKRS_NEW.DATA[0].FIELD.value
//use old payroll area if new payroll area does not exist
if ( Len(payarea) == 0 )
then
	payarea = data.Data_Page.CurrPosInfo.LeftSide.Payroll.PayrollTxt.rawValue
endif

//show or hide field
 if (payarea == "B2")
 then
	this.presence = "visible";
	//Check ISR control parameters for read/write access
 	if($record.CONTROL_PARAM.ISR_MODE == "DISPLAY" |
   		$record.CONTROL_PARAM.ISR_FORM_VIEW =="ISR_APPROVE" |
   	   	$record.FORM_SCENARIO_STAGE.DATA.FIELD == "APPROVE" |
   		$record.CONTROL_PARAM.ISR_REVIEW == "true" )  
	then 
		this.access = "readOnly"
	else
		this.access = "open"	
	endif
 else
	this.presence = "hidden";
 endif


var prate = this.rawValue
if ( Len(prate) == 0 )
then
	//this.rawValue = data.Data_Page.NewMgrInstr_SF.Salary.AnnualRate.rawValue / ( 1950 *  ( data.Data_Page.NewMgrInstr_SF.WorkScheduleSF.FTE.rawValue / 100.0 ) );
	prate = $record.I0008_BETRG.DATA[0].FIELD.value
	this.rawValue = prate
endif

