	//make table readonly
	data.CostOverride.sfCostOverrideLines.Table1.access   = "";
	data.CostOverride.sfCostOverrideLines.Table1.presence = "visible";
	//clear cost override table 
	data.CostOverride.sfCostOverrideLines.sf_COD_DATES.dCostOvr_begda.rawValue = ""
	data.CostOverride.sfCostOverrideLines.sf_COD_DATES.dCostOvr_endda.rawValue = ""
	for i=0 upto 4 step 1 do 
		data.CostOverride.sfCostOverrideLines.Table1.Row1[i].Cell1[0].rawValue = ""
		data.CostOverride.sfCostOverrideLines.Table1.Row1[i].Cell2[0].rawValue = ""
		data.CostOverride.sfCostOverrideLines.Table1.Row1[i].Cell3[0].rawValue = ""
		data.CostOverride.sfCostOverrideLines.Table1.Row1[i].Cell4[0].rawValue = ""
		data.CostOverride.sfCostOverrideLines.Table1.Row1[i].Cell5[0].rawValue = ""
		data.CostOverride.sfCostOverrideLines.Table1.Row1[i].Cell6[0].rawValue = ""
		data.CostOverride.sfCostOverrideLines.Table1.Row1[i].Cell7[0].rawValue = ""
		data.CostOverride.sfCostOverrideLines.Table1.Row1[i].Cell8[0].rawValue = ""
		data.CostOverride.sfCostOverrideLines.Table1.Row1[i].Cell9[0].rawValue = ""
	endfor
else
	data.CostOverride.sfCOD_EXIST.grpCOD_EXIST.rblstCOD_EXIST.rawValue = "3"
	//show cost override table and make it readonly
	data.CostOverride.sfCostOverrideLines.access = "readOnly";
	data.CostOverride.sfCostOverrideLines.presence = "visible";
endif

//Trigger call to backend for BAdI user command processing
ContainerFoundation_JS.SendMessageToContainer(event.target, "submit", "", "", "", "");
//Do not allow any further click event until server roundtrip is completed
this.access	= "readOnly";

//
if ( Len(Ltrim($record.COST_OVERRIDE_TEXT.DATA[0].FIELD)) > 0 )
then
	//cost override data exists
	//show cost override table
	data.CostOverride.sfCostOverrideLines.access   = "readOnly";
	data.CostOverride.sfCostOverrideLines.presence = "visible";
	//make dates readonly/visible
	data.CostOverride.sfCostOverrideLines.sf_COD_DATES.access   = "readOnly";
	data.CostOverride.sfCostOverrideLines.sf_COD_DATES.presence = "visible";
	//make table readonly/visible
	data.CostOverride.sfCostOverrideLines.Table1.access   = "readOnly";
	data.CostOverride.sfCostOverrideLines.Table1.presence = "visible";

	if (  data.CostOverride.sfCOD_EXIST.grpCOD_EXIST.rblstCOD_EXIST.rawValue == "1" )
	then
		//show cost override table edit/visible
		data.CostOverride.sfCostOverrideLines.access   = "";
		data.CostOverride.sfCostOverrideLines.presence = "visible";
		//extended dates make editable/visable
		data.CostOverride.sfCostOverrideLines.sf_COD_DATES.access   = "";
		data.CostOverride.sfCostOverrideLines.sf_COD_DATES.presence = "visible";
	endif
	if (  data.CostOverride.sfCOD_EXIST.grpCOD_EXIST.rblstCOD_EXIST.rawValue == "2" )
	then
		//new cost override to enter
		//show cost override table edit/visible
		data.CostOverride.sfCostOverrideLines.access   = "";
		data.CostOverride.sfCostOverrideLines.presence = "visible";
		//make dates editable/visible
		data.CostOverride.sfCostOverrideLines.sf_COD_DATES.access   = "";
		data.CostOverride.sfCostOverrideLines.sf_COD_DATES.presence = "visible";
		//make table editable/visible
		data.CostOverride.sfCostOverrideLines.Table1.access   = "";
		data.CostOverride.sfCostOverrideLines.Table1.presence = "visible";
	endif
else
	//cost override doesnot exist
	if (  data.CostOverride.sfCOD_NOEXIST.rbCOD_NOEXIST.rawValue == "N" )
	then
		//hide cost override table
		data.CostOverride.sfCostOverrideLines.access   = "readOnly";
		data.CostOverride.sfCostOverrideLines.presence = "hidden";
		//make dates read only
		data.CostOverride.sfCostOverrideLines.sf_COD_DATES.access   = "readOnly";
		data.CostOverride.sfCostOverrideLines.sf_COD_DATES.presence = "hidden";
		//make table readonly
		data.CostOverride.sfCostOverrideLines.Table1.access   = "readOnly";
		data.CostOverride.sfCostOverrideLines.Table1.presence = "hidden";
	else
		//show cost override table
		data.CostOverride.sfCostOverrideLines.access   = "";
		data.CostOverride.sfCostOverrideLines.presence = "visible";
		//make dates read only
		data.CostOverride.sfCostOverrideLines.sf_COD_DATES.access   = "";
		data.CostOverride.sfCostOverrideLines.sf_COD_DATES.presence = "visible";
		//make table readonly
		data.CostOverride.sfCostOverrideLines.Table1.access   = "";
		data.CostOverride.sfCostOverrideLines.Table1.presence = "visible";
	endif	 
endif

//set access for entire subform
if ( $record.CONTROL_PARAM.ISR_REVIEW == "true" )
then
	$.access = "readOnly"
else
	if ( $record.CONTROL_PARAM.ISR_FORM_VIEW == "ISR_REQUEST" |
	     $record.CONTROL_PARAM.ISR_FORM_VIEW == "ISR_PROCESS" )
    then
   		$.access = "open"
    else
   		$.access = "readOnly"
  	endif
endif

